import React, { useState } from 'react';

const agentsData = [
  { id: 1, domain: 'Sales', title: 'Smart Proposal Generation Tool', description: 'Speed up sales with automated, personalized proposal creation and tracking tools.', icon: '📄' },
  { id: 2, domain: 'Security', title: 'Automated Threat Detection and Response', description: 'Real-time automated threat detection and response with AI-powered solutions.', icon: '🛡️' },
  { id: 3, domain: 'Marketing', title: 'Real-Time Marketing Optimization', description: 'Enhance campaigns with real-time insights from audience sentiment and competitor data.', icon: '📊' },
  { id: 4, domain: 'E-Commerce', title: 'Dynamic E-Commerce Pricing Engine', description: 'Optimize pricing with AI for higher profits and real-time market adaptability.', icon: '💰' },
  { id: 5, domain: 'HR', title: 'AI-Powered Maintenance Alerts', description: 'Prevent IT downtime with AI-driven predictive maintenance and proactive alerts.', icon: '⚠️' },
  { id: 6, domain: 'Finance & Accounting', title: 'Automated Expense Auditing', description: 'Streamline expense audits with AI for accurate and real-time financial insights.', icon: '💼' },
  { id: 7, domain: 'Research', title: 'NFT Marketplace Trend Analysis', description: 'Analyze NFT trends with AI for actionable insights and smarter marketplace decisions.', icon: '📈' },
  { id: 8, domain: 'Automation', title: 'Real-Time Phishing Email Detection', description: 'Neutralize phishing threats with AI-driven real-time email analysis and detection.', icon: '📧' },
];

const AgentsPage = () => {
  const [selectedDomain, setSelectedDomain] = useState('All');

  const handleDomainClick = (domain) => {
    setSelectedDomain(domain);
  };

  const filteredAgents =
    selectedDomain === 'All'
      ? agentsData
      : agentsData.filter((agent) => agent.domain === selectedDomain);

  return (
    <div className="bg-gray-900 text-white min-h-screen px-6 py-12">
      <div className="container mx-auto">
        <h1 className="text-4xl font-bold mb-6">All Use Cases</h1>
        <div className="flex space-x-4 mb-8">
          {['All', 'Sales', 'Security', 'Marketing', 'E-Commerce', 'HR', 'Finance & Accounting', 'Research', 'Automation'].map((domain) => (
            <button
              key={domain}
              onClick={() => handleDomainClick(domain)}
              className={`px-4 py-2 rounded-md font-medium ${
                selectedDomain === domain ? 'bg-yellow-500 text-gray-900' : 'bg-gray-700 hover:bg-gray-600'
              }`}
            >
              {domain}
            </button>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredAgents.map((agent) => (
            <div key={agent.id} className="bg-gray-800 p-6 rounded-lg shadow-md">
              <div className="text-3xl mb-4">{agent.icon}</div>
              <h2 className="text-xl font-semibold mb-2">{agent.title}</h2>
              <p className="text-gray-400 mb-4">{agent.description}</p>
              <a
                href="#"
                className="text-yellow-500 font-medium hover:underline flex items-center"
              >
                Learn more <span className="ml-2">&rarr;</span>
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AgentsPage;
