import React from 'react';

const HeroSection = () => {
  return (
    <section className="bg-white py-16">
      <div className="container mx-auto px-6 text-center">
        <h1 className="text-4xl font-extrabold text-gray-800">Revolutionize Work with AI-Powered Efficiency</h1>
        <p className="mt-4 text-lg text-gray-600">Empower your team. Deploy your first AI agent today!</p>
        <p className="mt-2 text-gray-500">Scale smarter with AI teammates handling tasks seamlessly. Boost outcomes, not overhead.</p>
        <div className="mt-6 flex justify-center space-x-4">
          <a href="#try" className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-700">Try for Free</a>
          <a href="#demo" className="bg-gray-600 text-white px-6 py-3 rounded hover:bg-gray-700">Request Demo</a>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
